mapper.update:
UPDATE system_user_role
SET deleted = 1
WHERE org_id = #{orgId}
  AND deleted = 0
  AND app_id IN

      #{id}

UPDATE system_user_role
SET deleted = 1
WHERE user_id = #{userId}
  AND org_id = #{orgId}
  AND deleted = 0

UPDATE system_user_role
SET deleted = 1
WHERE user_id = #{userId}
  AND org_id = #{orgId}
  AND role_id = #{roleId}
  AND deleted = 0

UPDATE system_user_role
SET asset_scope = #{assetScope}
WHERE user_id = #{userId}
  AND org_id = #{orgId}
  AND deleted = 0
