mapper.select:
SELECT COUNT(1)
FROM system_user_org
WHERE org_id = #{orgId}
  AND user_id = #{userId}
  AND identity_type = #{identityType}
  AND deleted = 0

SELECT user_id
FROM system_user_org
WHERE org_id = #{orgId}
  AND identity_type = #{identityType}
  AND deleted = 0

SELECT org_id
FROM system_user_org
WHERE user_id = #{userId}
  AND identity_type = #{identityType}
  AND deleted = 0

mapper.update:
UPDATE system_user_org
SET deleted = 1
WHERE org_id = #{orgId}
  AND identity_type = #{identityType}
  AND deleted = 0

UPDATE system_user_org
SET deleted = 1
WHERE org_id = #{orgId}
  AND user_id = #{userId}
  AND identity_type = #{identityType}
  AND deleted = 0

